		WHERE cd_approval_status = NEW.cd_approval_status;

	IF fl_approved_n = 'Y' AND ( NEW.cd_employee_approver IS NULL OR NEW.dt_approval_date IS NULL  OR NEW.dt_start_at IS NULL) THEN
