               p.dt_end
          from tmpbenefemployee e, tmpbenefperiod p;
        DECLARE CONTINUE HANDLER FOR NOT FOUND SET done2 = 1;

        OPEN cur2;

        FETCH cur2 INTO cur_cd_employee,
                        cur_ds_period, 
